import React from 'react';
import { Col, Form, FormGroup, Input, Label, Row } from 'reactstrap';
import Profile from './Profile';
import TableProfile from './TableProfile';

function ProfileInput (){
     const [state, setState] = React.useState({
        nama : "",
        age : "",
        adress : "",
        listProfile: [],
    });

    // const saveHandle =(e)=>{
    //     e.prevenDefault()
    // }
   const toggle = (data) => {
        setState({
            nama: '',
            age: '',
            adress: ''
        })
        setState({ listProfile: data })
        // console.log(this.state.listProfile)
    }

        const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        state[name] = value;
        setState(state);
      }

    return (
        <div className="App" >
            <Row>
                <Col md="6">
                    <h1>Profile Input</h1>
                    <Form id="formBiodata">
                        <FormGroup>
                            <Label for="name"><b>Name</b></Label>
                            <Input type="text" name="name" id="name"
                                autoComplete='off'
                                placeholder="Name..."
                                required 
                                onChange={handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="age"><b>Age</b></Label>
                            <Input type="text" name="age" id="age"
                                autoComplete='off'
                                placeholder="Age..."
                                required 
                                onChange={handleChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="adress"><b>Adress</b></Label>
                            <Input type="text" name="adress" id="adress"
                                autoComplete='off'
                                placeholder="Adress..."
                                required 
                                onChange={handleChange} />
                        </FormGroup>
                    </Form>
                </Col>
                <Col md="6">
                    <Profile
                        name={state.name}
                        age={state.age}
                        adress={state.adress}
                        reset={(data) => {toggle(data) }}
                    />
                </Col>
            </Row>
            <TableProfile listProfile={state.listProfile} key={state.listProfile.length}/>
        </div >
    )
}

export default ProfileInput;
