import React, { useState } from "react";
import { Card, CardBody, CardHeader, Button, FormGroup, Form, Label, Input } from 'reactstrap';


function Profile() {
    const [formProfile, setFormProfile] = React.useState({
        name : "",
        age : "",
        adress : "",
    })

    const saveHandle =(e)=>{
        e.prevenDefault()
    }

    const handleChange = (e) => {
        const name = e.target.name;
        const value = e.target.value;
        formProfile[name] = value;
        setFormProfile(formProfile);
      }




    return (
        <>
            <Card bg="primary" style={{ height: '300px' }} >
                <CardHeader style={{ backgroundColor: '#47CDF1' }}><h1>Profile</h1></CardHeader>
                <CardBody>
                   <Form>
                       <FormGroup>
                       <Label for="nama"><b>Nama</b></Label>
                            <Input type="text" name="nama" id="nama"
                                placeholder="Position"
                                 required
                                onChange={handleChange} />
                       </FormGroup>
                   </Form>
                </CardBody>
                <div style={{ float: 'right' }}>
                    <Button
                        className="btn-pill btn-lg float-right"
                        style={{ background: "#9ACD32", border: "none", width: '100px' }}
                        onClick={() => { saveHandle()}}
                    >
                        Save
                        </Button>
                </div>
            </Card>

        </>
    );
}

export default Profile;
