import React from 'react';
import { Form, FormGroup, Input, Label, Row } from 'reactstrap';
import Profile from './Profile';
import TableProfile from './TableProfile';

const ProfileInput = () =>{
   state = {
        name: '',
        age: '',
        adress: '',
        listProfile: [],
    }

   const toggle = (data) => {
        this.setState({
            name: '',
            age: '',
            adress: ''
        })
        this.setState({ listProfile: data })
        // console.log(this.state.listProfile)
    }

    const dataChange = event => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    return (
        <div className="App" >
            <Row>
                <Col md="6">
                    <h1>Profile Input</h1>
                    <Form id="formBiodata">
                        <FormGroup>
                            <Label for="name"><b>Name</b></Label>
                            <Input type="text" name="name" id="name"
                                autoComplete='off'
                                placeholder="Name..."
                                value={state.name} required
                                onChange={dataChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="age"><b>Age</b></Label>
                            <Input type="text" name="age" id="age"
                                autoComplete='off'
                                placeholder="Age..."
                                value={state.age} required
                                onChange={dataChange} />
                        </FormGroup>
                        <FormGroup>
                            <Label for="adress"><b>Adress</b></Label>
                            <Input type="text" name="adress" id="adress"
                                autoComplete='off'
                                placeholder="Adress..."
                                value={state.adress} required
                                onChange={dataChange} />
                        </FormGroup>
                    </Form>
                </Col>
                <Col md="6">
                    <Profile
                        name={state.name}
                        age={state.age}
                        adress={state.adress}
                        reset={(data) => {toggle(data) }}
                    />
                </Col>
            </Row>
            <TableProfile listProfile={state.listProfile} key={state.listProfile.length}/>
        </div >
    )
}

export default ProfileInput;
